import React, { useState } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import SignatureCanvas from 'react-signature-canvas';

const fieldTypes = [
  { type: "text", label: "Text Input" },
  { type: "textarea", label: "Text Area" },
  { type: "number", label: "Number Input" },
  { type: "select", label: "Dropdown" },
  { type: "radio", label: "Radio Buttons" },
  { type: "checkbox", label: "Checkboxes" },
  { type: "date", label: "Date Picker" },
  { type: "file", label: "File Upload" },
  { type: "signature", label: "Signature Fields" },
];

const DraggableField = ({ field }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "FIELD",
    item: field,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div ref={drag} className="p-2 border bg-light mb-2 w-100" style={{ opacity: isDragging ? 0.5 : 1 }}>
      {field.label}
    </div>
  );
};

const DroppableField = ({ field, index, moveField, handleLabelChange, handleOptionAdd, handleDelete }) => {
  const [, drag] = useDrag(() => ({
    type: "FORM_FIELD",
    item: { index },
  }));

  const [, drop] = useDrop(() => ({
    accept: "FORM_FIELD",
    hover: (draggedItem) => {
      if (draggedItem.index !== index) {
        moveField(draggedItem.index, index);
        draggedItem.index = index;
      }
    },
  }));

  return (
    <div ref={(node) => drag(drop(node))} className="d-flex align-items-center gap-2 p-2 bg-white w-100">
      <input
        type="text"
        className="form-control mx-2"
        value={field.label}
        onChange={(e) => handleLabelChange(field.id, e.target.value)}
        style={{ width: "150px" }}
      />
      {field.type === "text" && <input type="text" className="form-control" />} 
      {field.type === "textarea" && <textarea className="form-control"></textarea>}
      {field.type === "number" && <input type="number" className="form-control" />} 
      
      {field.type === "select" && (
        <div className="d-flex w-100">
          <select className="form-control w-75">
            <option>Option 1</option>
            <option>Option 2</option>
            {field.options.map((opt, i) => (
              <option key={i}>{opt}</option>
            ))}
          </select>
          <button className="btn btn-sm btn-primary mx-2" onClick={() => handleOptionAdd(field.id)}>
            <i className="fa-solid fa-plus"></i>
          </button>
        </div>
      )}
      
      {field.type === "signature" && (
        <div>
          <SignatureCanvas penColor="black" canvasProps={{ width: 400, height: 100, className: "border" }} />
        </div>
      )}
      
      <button className="btn btn-sm btn-danger mx-2" onClick={() => handleDelete(field.id)}>
        <i className="fa-solid fa-trash"></i>
      </button>
    </div>
  );
};

const DroppableForm = ({ fields, setFields }) => {
  const [, drop] = useDrop(() => ({
    accept: "FIELD",
    drop: (item) => setFields((prevFields) => [...prevFields, { ...item, id: Date.now(), label: item.label, options: [] }]),
  }));

  const moveField = (fromIndex, toIndex) => {
    setFields((prevFields) => {
      const updatedFields = [...prevFields];
      const [movedField] = updatedFields.splice(fromIndex, 1);
      updatedFields.splice(toIndex, 0, movedField);
      return updatedFields;
    });
  };

  const handleLabelChange = (id, newLabel) => {
    setFields((prevFields) => prevFields.map((field) => (field.id === id ? { ...field, label: newLabel } : field)));
  };

  const handleOptionAdd = (id) => {
    setFields((prevFields) =>
      prevFields.map((field) =>
        field.id === id ? { ...field, options: [...field.options, `Option ${field.options.length + 3}`] } : field
      )
    );
  };

  const handleDelete = (id) => {
    setFields((prevFields) => prevFields.filter((field) => field.id !== id));
  };

  return (
    <div ref={drop} className="border p-3 bg-white w-100" style={{ minHeight: "440px" }}>
      <div className="d-flex flex-column gap-3">
        {fields.map((field, index) => (
          <DroppableField
            key={field.id}
            field={field}
            index={index}
            moveField={moveField}
            handleLabelChange={handleLabelChange}
            handleOptionAdd={handleOptionAdd}
            handleDelete={handleDelete}
          />
        ))}
      </div>
    </div>
  );
};

const Forms = () => {
  const [fields, setFields] = useState([]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="container mt-3">
        <div className="row">
          <div className="col-md-3 col-sm-12">
            <h4 className="fw-bolder text-center mb-3">Form Fields</h4>
            <div className="d-flex flex-wrap justify-content-center">
              {fieldTypes.map((field, index) => (
                <DraggableField key={index} field={field} />
              ))}
            </div>
          </div>
          <div className="col-md-8 col-sm-12">
            <h4 className="fw-bolder text-center mb-3">Drop Fields Here</h4>
            <DroppableForm fields={fields} setFields={setFields} />
          </div>
        </div>
      </div>
    </DndProvider>
  );
};

export default Forms;
