import React, { useState } from "react";
import { DndProvider, useDrag } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

const fieldTypes = [
  { type: "text", label: "Text Input" },
  { type: "textarea", label: "Text Area" },
  { type: "number", label: "Number Input" },
  { type: "select", label: "Dropdown" },
  { type: "radio", label: "Radio Buttons" },
  { type: "checkbox", label: "Checkboxes" },
  { type: "date", label: "Date Picker" },
  { type: "file", label: "File Upload" },
];

const DraggableField = ({ field }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "FIELD",
    item: field,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div ref={drag} className="p-2 border bg-light mb-2 w-75" style={{ opacity: isDragging ? 0.5 : 1 }}>
      {field.label}
    </div>
  );
};

export default function Forms (){
  const [fields, setFields] = useState([]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="container mt-3">
        <div className="row">
          <div className="col-md-3">
            <h4 className="fw-bolder text-center mb-3">Form Fields</h4>
            <span className="justify-content-center d-flex flex-wrap">
            {fieldTypes.map((field, index) => (
              <DraggableField key={index} field={field} />
            ))}
            </span>
          </div>
          <div className="col-md-8">
            <h4 className="fw-bolder text-center mb-3">Drop Fields Here</h4>
            <div className="border p-3 bg-white" style={{ minHeight: "400px" }}>
              {fields.map((field, index) => (
                <div key={index} className="d-flex align-items-center gap-2 p-2 bg-white">
                  <input type="text" className="form-control" value={field.label} readOnly style={{ width: "150px" }} />
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </DndProvider>
  );
};
