import React, { useState } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import SignatureCanvas from "react-signature-canvas";

const fieldTypes = [
  { type: "text", label: "Text Input" },
  { type: "textarea", label: "Text Area" },
  { type: "number", label: "Number Input" },
  { type: "select", label: "Dropdown" },
  { type: "radio", label: "Radio Buttons" },
  { type: "checkbox", label: "Checkboxes" },
  { type: "date", label: "Date Picker" },
  { type: "file", label: "File Upload" },
  { type: "signature", label: "Signature Fields" },
];

const DraggableField = ({ field }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "FIELD",
    item: field,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div ref={drag} className="p-2 border bg-light mb-2 w-100" style={{ opacity: isDragging ? 0.5 : 1 }}>
      {field.label}
    </div>
  );
};

const DroppableField = ({ field, index, moveField, handleLabelChange, handleOptionAdd, handleOptionRemove, handleDelete }) => {
  const [, drag] = useDrag(() => ({
    type: "FORM_FIELD",
    item: { index },
  }));

  const [, drop] = useDrop(() => ({
    accept: "FORM_FIELD",
    hover: (draggedItem) => {
      if (draggedItem.index !== index) {
        moveField(draggedItem.index, index);
        draggedItem.index = index;
      }
    },
  }));

  return (
    <div ref={(node) => drag(drop(node))} className="d-flex align-items-center gap-2 p-2 bg-white w-100 position-relative">
      <input
        type="text"
        className="form-control mx-2"
        value={field.label}
        onChange={(e) => handleLabelChange(field.id, e.target.value)}
        style={{ width: "150px" }}
      />
      {field.type === "text" && <input type="text" className="form-control" />}
      {field.type === "textarea" && <textarea className="form-control"></textarea>}
      {field.type === "number" && <input type="number" className="form-control" />}
      {field.type === "select" && (
        <div className="d-flex justify-content-between align-items-center w-100">
          <select className="form-control w-75">
            {field.options.map((opt, i) => (
              <option key={i}>{opt}</option>
            ))}
          </select>
          <div className="d-flex">
            <button className="btn btn-sm btn-secondary mx-1" onClick={() => handleOptionAdd(field.id)}>+</button>
            <button className="btn btn-sm btn-danger" onClick={() => handleOptionRemove(field.id)} disabled={field.options.length === 0}>-</button>
          </div>
        </div>
      )}
      {field.type === "radio" && (
        <div>
          {field.options.map((opt, i) => (
            <label key={i} className="me-2">
              <input type="radio" name={`radio-${field.id}`} /> {opt}
            </label>
          ))}
          <button className="btn btn-sm btn-secondary mx-1" onClick={() => handleOptionAdd(field.id)}>+</button>
          <button className="btn btn-sm btn-danger" onClick={() => handleOptionRemove(field.id)} disabled={field.options.length === 0}>-</button>
        </div>
      )}
      {field.type === "checkbox" && (
        <div>
          {field.options.map((opt, i) => (
            <label key={i} className="me-2">
              <input type="checkbox" /> {opt}
            </label>
          ))}
          <button className="btn btn-sm btn-secondary mx-1" onClick={() => handleOptionAdd(field.id)}>+</button>
          <button className="btn btn-sm btn-danger" onClick={() => handleOptionRemove(field.id)} disabled={field.options.length === 0}>-</button>
        </div>
      )}
      {field.type === "date" && <input type="date" className="form-control" />}
      {field.type === "file" && <input type="file" className="form-control" />}
      {field.type === "signature" && <SignatureCanvas penColor="black" canvasProps={{ width: 400, height: 100, className: "border" }} />}
      <i className="fa-solid fa-xmark text-white rounded-circle bg-danger position-absolute" style={{ cursor: "pointer", fontSize: "10px", marginTop: "-38px", marginLeft: "3px", padding: "2px" }} onClick={() => handleDelete(field.id)}></i>
    </div>
  );
};

const DroppableForm = ({ fields, setFields, saveForm }) => {
  const [, drop] = useDrop(() => ({
    accept: "FIELD",
    drop: (item) =>
      setFields((prevFields) => [...prevFields, { ...item, id: Date.now(), label: item.label, options: ["Option 1", "Option 2"] }]),
  }));

  return (
    <div ref={drop} className="border p-3 bg-white w-100" style={{ minHeight: "440px" }}>
      {fields.map((field, index) => (
        <DroppableField key={field.id} field={field} index={index} moveField={(from, to) => {
          const updated = [...fields];
          const [moved] = updated.splice(from, 1);
          updated.splice(to, 0, moved);
          setFields(updated);
        }} handleLabelChange={(id, newLabel) => {
          setFields(fields.map(f => f.id === id ? { ...f, label: newLabel } : f));
        }} handleOptionAdd={(id) => {
          setFields(fields.map(f => f.id === id ? { ...f, options: [...f.options, `Option ${f.options.length + 1}`] } : f));
        }} handleOptionRemove={(id) => {
          setFields(fields.map(f => f.id === id ? { ...f, options: f.options.slice(0, -1) } : f));
        }} handleDelete={(id) => {
          setFields(fields.filter(f => f.id !== id));
        }} />
      ))}
      <button className="btn btn-primary mt-3" onClick={saveForm}>Save Form</button>
    </div>
  );
};

const FormPreview = ({ savedFields }) => {
  return (
    <div className="container mt-3">
      <h3>Form Preview</h3>
      {savedFields.map((field, index) => (
        <div key={index} className="mb-3">
          <label>{field.label}</label>
          {field.type === "text" && <input type="text" className="form-control" />}
          {field.type === "textarea" && <textarea className="form-control"></textarea>}
          {field.type === "number" && <input type="number" className="form-control" />}
          {field.type === "select" && (
            <select className="form-control">
              {field.options.map((opt, i) => <option key={i}>{opt}</option>)}
            </select>
          )}
        </div>
      ))}
    </div>
  );
};

const Forms = () => {
  const [fields, setFields] = useState([]);
  const [savedFields, setSavedFields] = useState(null);

  return (
    <DndProvider backend={HTML5Backend}>
      {savedFields ? <FormPreview savedFields={savedFields} /> : <DroppableForm fields={fields} setFields={setFields} saveForm={() => setSavedFields(fields)} />}
    </DndProvider>
  );
};

export default Forms;
